import * as React from "react"

import { cn } from "@/lib/utils"
import { FbLabel } from "@/registry/fly-by/ui/fb-label"

/**
 * fly-by Field
 *
 * シンプルなフォームフィールドラッパー。ラベル、説明、エラーメッセージを統合。
 * react-hook-form に依存しない軽量版。
 *
 * クラス構造: field, fb-field-label, fb-field-description, fb-field-error
 *
 * @example
 * <Field>
 *   <FieldLabel>メールアドレス</FbFieldLabel>
 *   <FbInput type="email" />
 *   <FieldDescription>連絡先として使用します。</FbFieldDescription>
 * </Field>
 *
 * @example
 * <Field error="必須項目です">
 *   <FieldLabel>名前</FbFieldLabel>
 *   <FbInput />
 * </Field>
 */

interface FbFieldContextValue {
  id: string
  hasError: boolean
}

const FbFieldContext = React.createContext<FieldContextValue | null>(null)

const useFbField = () => {
  const context = React.useContext(FbFieldContext)
  if (!context) {
    throw new Error("useFbField must be used within <Field>")
  }
  return context
}

interface FieldProps extends React.HTMLAttributes<HTMLDivElement> {
  error?: string
}

function Field({ className, error, children, ...props }: FieldProps) {
  const id = React.useId()

  return (
    <FieldContext.Provider value={{ id, hasError: !!error }}>
      <div
        data-slot="field"
        className={cn("field space-y-2", className)}
        {...props}
      >
        {children}
        {error && (
          <p
            data-slot="field-error"
            className="field-error text-[0.8rem] font-medium text-red-600 dark:text-red-400"
          >
            {error}
          </p>
        )}
      </div>
    </FbFieldContext.Provider>
  )
}

function FbFieldLabel({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof FbLabel>) {
  const { id, hasError } = useFbField()

  return (
    <FbLabel
      data-slot="field-label"
      htmlFor={id}
      className={cn(
        "field-label",
        hasError && "text-red-600 dark:text-red-400",
        className
      )}
      {...props}
    />
  )
}

function FbFieldDescription({
  className,
  ...props
}: React.HTMLAttributes<HTMLParagraphElement>) {
  return (
    <p
      data-slot="field-description"
      className={cn(
        "field-description text-[0.8rem] text-gray-500 dark:text-gray-400",
        className
      )}
      {...props}
    />
  )
}

export { Field, FbFieldLabel, FbFieldDescription, useFbField }
